import React from 'react'
import './App.css'
import firebase from 'firebase';
import 'firebase/firestore';

import SidebarComponent from './sidebar/sidebar'
import EditorComponent from './editor/editor'

// Clase principal de React

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      // Variables de estado
      selectedNoteIndex: null,
      selectedNote: null,
      notes: null,
    }
  }

  render() {
    // Página principal de la aplicación
    return(
      <div className='app-container'>
        <SidebarComponent
         selectedNoteIndex={this.state.selectedNoteIndex}
         notes={this.state.notes}
         deleteNote={this.deleteNote}
         selectNote={this.selectNote}
         newNote={this.newNote}
         ></SidebarComponent>
        {
          this.state.selectedNote ? // Si ha seleccionado una nota
            <EditorComponent
            selectedNote={this.state.selectedNote}
            selectedNoteIndex={this.state.selectedNoteIndex}
            notes={this.state.notes}
            noteUpdate={this.noteUpdate}
          ></EditorComponent>
          : // Si no ha seleccionada nada no despliega el editor
          null
        }
      </div>
    )
  }

  // Nueva nota
  newNote = async (title) => {
    const note = {
      title: title,
      body: "",
    }
    const newFromDB = await firebase
    .firestore()
    .collection('notes')
    .add({
      title: note.title,
      body: note.body,
      timestamp: firebase.firestore.FieldValue.serverTimestamp() // Fecha de creación
    })
    const newID = newFromDB.id;
    await this.setState({ notes: [...this.state.notes, note]}) // Actualiza el estado con las notas que ya existen más la nueva nota
    const newNoteIndex = this.state.note.indexOf(this.state.notes.filter(_note => _note.id === newID)[0]) // Obtiene el nuevo index
    this.setState({
      selectedNote: this.state.notes[newNoteIndex],
      selectedNoteIndex: newNoteIndex
    }) // Selecciona el nuevo index
  }

  // Actualiza en la base de datos
  noteUpdate = (id, note) => {
    firebase
    .firestore()
    .collection('notes')
    .doc(id)
    .update({
      title: note.title,
      body: note.body,
      timestamp: firebase.firestore.FieldValue.serverTimestamp() // Última vez editado
    })
  }

  // Selecciona la nota
  selectNote = (n, i) =>{
    this.setState({
      selectedNoteIndex: i,
      selectedNote: n
    })
  }

  // Cuando el cargue satisfactoriamente se llama automaticamente
  componentDidMount = () => {
    firebase
      .firestore()
      .collection('notes') // Obtiene lo que encuentre en notes
      .onSnapshot(serverUpdate => { // Cada vez que se actualice la base de datos
        const notes = serverUpdate.docs.map(_doc => { // Mapeado
          const data = _doc.data()
          data['id'] = _doc.id
          return data
        })
        //console.log(notes)
        this.setState({ notes: notes })
      })
  }
}

export default App
