import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import styles from './styles'
import List from '@material-ui/core/List'
import { Divider, Button } from '@material-ui/core'
import SidebarItemComponent from '../sidebarItem/sidebarItem'

// Barra lateral del proyecto

class SidebarComponent extends React.Component {
  constructor() {
    super()
    this.state = {
      addingNote: false,
      title: null,
    }
  }

  render() {
    // Enviados a través de App.js
    const { notes, classes, selectedNoteIndex } = this.props

    return (
      <div className={classes.sidebarContainer}>
        {/*Añade una nueva nota*/}
        <Button onClick={this.newNoteBtnClick} className={classes.newNoteBtn}>
          Nueva nota
        </Button>
        {
          // Si esta añadiendo una nueva nota
          this.state.addingNote ? (
            <div>
              <input
                type="text"
                className={classes.newNoteInput}
                placeholder="Título"
              ></input>
            </div>
          ) : // Si no no hace nada
          null
        }
      </div>
    )
  }

  newNoteBtnClick = () => {
    console.log('CREANDO UNA NUEVA NOTA')
  }
}

export default withStyles(styles)(SidebarComponent) // Exporta el componente con estilos
