import React from 'react'
import ReactQuill from 'react-quill'
import debounce from '../helpers'
import BorderColorIcon from '@material-ui/icons/BorderColor'
import {withStyles} from '@material-ui/core/styles'
import styles from './styles'

// Componente editor del aplicativo

class EditorComponent extends React.Component{
    constructor(){
        super()
        this.state = {
            text: '',
            title: '',
            id: ''
        }
    }

    async imageHandler() {  
        const input = document.createElement('input');  
      
        input.setAttribute('type', 'file');  
        input.setAttribute('accept', 'image/*');  
        input.click();  
      
        input.onchange = async () => {  
          var file: any = input.files[0];  
          var formData = new FormData();  
      
          formData.append('image', file);  
      
          var fileName = file.name;  
      
          const res = await this.uploadFiles(file, fileName, quillObj);  
        };  
      } 

    render(){
        
        const {classes} = this.props

        var quillObj: any

        return(
        <div className = {classes.editorContainer}>
            <ReactQuill  
              ref={(el) => {  
                quillObj = el;  
              }}  
              modules={{  
                toolbar: {  
                  container: [  
                    [{ 'header': [1, 2, 3, 4, 5, 6, false] }],  
                    ['bold', 'italic', 'underline'],  
                    [{ 'list': 'ordered' }, { 'list': 'bullet' }],  
                    [{ 'align': [] }],  
                    ['link', 'image'],  
                    ['clean'],  
                    [{ 'color': [] }]  
                  ],  
                  handlers: {  
                    image: this.imageHandler  
                  }  
                },  
                table: true  
              }}  
              placeholder="Add a description of your event"  
              onChange={(content, delta, source, editor) => this.onDescriptionChange(content, editor)}  
              id="txtDescription"  
            />
        </div>
        )
    }
}

export default withStyles(styles)(EditorComponent) // Exporta el componente con estilos
