// Estilos del editor, dicha clase representa el estilo del editor
const styles = theme => ({
    canvasContainer: {
        marginTop: '0px',
        width: '300px',
        height: '100%',
        boxSizing: 'border-box',
        float: 'right',
        overflowY: 'scroll',
        overflowX: 'hidden'
    }
  });
  
  export default styles;
