import React from 'react'
import './App.css'
import db from './index.js'
import { getFirestore, collection, getDocs } from 'firebase/firestore/lite'

// Clase principal de React

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      // Variables de estado
      selectedNoteIndex: null,
      selectNote: null,
      notes: null,
    }
  }

  render() {
    // Página principal de la aplicación
    return <div>Hello world</div>
  }

  // Cuando el cargue satisfactoriamente se llama automaticamente
  componentDidMount = () => {
    // Obtiene todas las notas de firebase
    const notes = collection(db, 'notes') // De notas en la base de datos
    const notesSnapshot = getDocs(notes) // Cuando algo se actualiza dentro de notes
    const data = notesSnapshot.docs.map((_doc) => {
      // Mapeado
      const data = _doc.data() // Obtiene la información
      data['id'] = _doc.id
      return data // Devuelve un arreglo con todas las notas
    })
    console.log(notes)
    this.setState({ notes: data })
  }
}

export default App
